import React, { Component } from 'react'
import ButtonAction from '../components/ButtonAction'

export class Table extends Component {

  render() {
    const { data: { thead, tbody } } = this.props;

    return (
      <div className="card-body pad">
        <div className="mb-3">
          <table id="example2" className="table table-bordered table-hover">
            <thead>
              <tr>
                {thead.map((head, index) => <th key={index}>{head}</th>)}
                <th >Action</th>
              </tr>
            </thead>
            <tbody>
              {tbody.map((body, idx) => {
                console.log(body)
                return (
                  <tr key={idx}>
                    {thead.map((head, idxx) => <td key={idxx}>
                      {<img src={body[head.Icon]} alt={body[head]} /> ? <img src={body[head.Icon]} alt={body[head]} /> : body[head] }
                    </td>)}
                    <td>
                      <ButtonAction type="button" title='Edit' class="btn btn-outline-warning btn-sm" icon='fas fa-edit' /> &nbsp;
                      <ButtonAction type="button" title='Delete' class="btn btn-outline-danger btn-sm" icon='fas fa-trash' />
                    </td>
                  </tr>
                )
              })}
            </tbody>
            {/* <tfoot>
              <tr>
                <th>No</th>
                <th>Name</th>
                <th>Icon</th>
              </tr>
            </tfoot> */}
          </table>
        </div>
      </div>
    )
  }
}

export default Table
